package Lec1;

public class Maximum_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 111;
		int b = 113;
		int c = 113;
		if (a >= b && a >= c) {
			System.out.println(a);
		} else if (b >= a && b >= a) {
			System.out.println(b);
		} else {
			System.out.println(c);
		}
	}

}
